require "test_helper"
require "problem8"

class TestEuler < Minitest::Test

  def test_zero
    assert_equal 0, 0.greatest_product_of_consecutive_digits(2)
  end

  def test_invalid_digit_count
    assert_raises(ArgumentError) do 
      1.greatest_product_of_consecutive_digits(0)
    end
  end

  def test_small_problem
    assert_equal 36, 419451.greatest_product_of_consecutive_digits(2)
  end

  def test_actual_problem

    input = 
    assert_equal 40824, input.greatest_product_of_consecutive_digits(5)

  end

end
